export const PHRASE_COUNT = PHRASE_LIBRARY.length;

/**
 * Find phrase matches in text
 * Returns matches sorted by length (longer = more specific = higher priority)
 */
export function findPhraseMatches(text: string): PhraseMatch[] {
  const lowerText = text.toLowerCase();
  const matches: PhraseMatch[] = [];
  
  for (const entry of PHRASE_LIBRARY) {
    if (lowerText.includes(entry.phrase)) {
      matches.push(entry);
    }
  }
  
  // Already sorted by length in PHRASE_LIBRARY, but re-sort matches
  return matches.sort((a, b) => b.phrase.length - a.phrase.length);
}

/**
 * Get unique references (removes duplicate refs, keeps longest phrase)
 */
export function getUniqueReferences(matches: PhraseMatch[]): PhraseMatch[] {
  const seen = new Set<string>();
  return matches.filter(match => {
    if (seen.has(match.reference)) {
      return false;
    }
    seen.add(match.reference);
    return true;
  });
}
